import React, { Component } from 'react'
import HouseService from '../services/HouseService'

class EditHouse extends Component {
  constructor(props){
    super(props)
    this.state = {
      house : {
        address: '',
        squareMetres: '',
        description: '',
        photos : ''
      }
    }
  }

  handleFormSubmit = (event) => {
    event.preventDefault()
    HouseService.editHouse(this.state.house)
  }

  handleChange = (event) => {
    const {name, value, files} = event.target
    this.setState({
      house: {
        ...this.state.house,
      [name]: files && files[0] ? files[0] : value
      }

    })

  }

  render() {
    return(
      <div>
        <h1>this is edit house</h1>
        <form onSubmit={this.handleFormSubmit}>
          <input name='address' type='text' placeholder='adress' onChange={(e) => this.handleChange(e)} ></input>
          <input name='squareMeters' type='number' placeholder='size' onChange></input>
          <input name='description' type='textbox' placeholder='write a description' ></input>
          <input name='photo' type='file' placeholder='search image' ></input>
          <button type='submit'>edit</button>
        </form>
      </div>
    )
  }
}

export default EditHouse
