import React, { Component } from 'react'
import UserService from '../services/UserService'

class Favourites extends Component {
  constructor(props){
    super(props)
    this.state = {
      favourites: [],
      deletingTarget: ''
    }
  }

  fetchFavourites = () => {
    UserService.listFavs()
    .then(favourites => 
      this.setState({
         favourites: favourites
      })
    )
  }

  componentDidMount() {
    this.fetchFavourites()
  }

  // deleteFav = () => {
  //   UserService.deleteFavourite(this.state.deletingTarget)
  // }
  // }

  render() {
    return(
      <div>
        <h1>this is favs</h1>
        {this.state.favourites.map(fav =>
          <div> 
            <h3>{fav.address}</h3>
            <button>delete favourite</button>
          </div>
        )}
      </div>


    )
  }
}

export default Favourites
